import { useState } from "react";
import ProjectCard from "./ProjectCard";
import { Button } from "../components/ui/shadcn-button";

const ProjectsSection = () => {
  const [activeFilter, setActiveFilter] = useState("All");

  const projects = [
    {
      id: 1,
      title: "Digital Literacy Program",
      description: "Empowering communities through technology access and digital skills training.",
      status: "Ongoing",
      category: "Education",
      image: "https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=800&h=600&fit=crop"
    },
    {
      id: 2,
      title: "Rural Health Initiative",
      description: "Providing essential healthcare services to underserved rural communities.",
      status: "Completed",
      category: "Healthcare",
      image: "https://images.unsplash.com/photo-1559757148-5c350d0d3c56?w=800&h=600&fit=crop"
    },
    {
      id: 3,
      title: "Clean Water Project",
      description: "Installing sustainable water filtration systems in remote villages.",
      status: "Ongoing",
      category: "Environment",
      image: "https://images.unsplash.com/photo-1518005020951-eccb494ad742?w=800&h=600&fit=crop"
    },
    {
      id: 4,
      title: "School Infrastructure Development",
      description: "Building and renovating educational facilities for better learning environments.",
      status: "Completed",
      category: "Education",
      image: "https://images.unsplash.com/photo-1527576539890-dfa815648363?w=800&h=600&fit=crop"
    },
    {
      id: 5,
      title: "Mobile Health Clinics",
      description: "Bringing medical care directly to communities through mobile healthcare units.",
      status: "Ongoing",
      category: "Healthcare",
      image: "https://images.unsplash.com/photo-1576091160399-112ba8d76b6e?w=800&h=600&fit=crop"
    },
    {
      id: 6,
      title: "Reforestation Campaign",
      description: "Restoring ecosystems through community-led tree planting initiatives.",
      status: "Ongoing",
      category: "Environment",
      image: "https://images.unsplash.com/photo-1441974231531-c6227db76b6e?w=800&h=600&fit=crop"
    }
  ];

  const filters = ["All", "Education", "Healthcare", "Environment"];

  const filteredProjects = activeFilter === "All" 
    ? projects 
    : projects.filter(project => project.category === activeFilter);

  return (
    <section className="py-20 bg-gray-900">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-bold text-white mb-4">
            Our <span className="text-teal-500">Projects</span>
          </h2>
          <p className="text-xl text-gray-300 max-w-2xl mx-auto">
            Transforming communities through innovative solutions and sustainable impact.
          </p>
        </div>

        {/* Filter Buttons */}
        <div className="flex justify-center mb-12">
          <div className="flex gap-4 flex-wrap">
            {filters.map((filter) => (
              <Button
                key={filter}
                variant={activeFilter === filter ? "default" : "outline"}
                onClick={() => setActiveFilter(filter)}
                className={`px-6 py-2 transition-all duration-300 ${
                  activeFilter === filter
                    ? "bg-blue-600 text-white hover:bg-blue-700"
                    : "border-gray-600 text-gray-300 bg-transparent hover:bg-teal-600/20 hover:text-white hover:border-teal-600"
                }`}
              >
                {filter}
              </Button>
            ))}
          </div>
        </div>
        
        {/* Projects Grid */}
        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Left Column - First 3 projects */}
            <div className="space-y-10">
              {filteredProjects.slice(0, 3).map((project) => (
                <div key={project.id} className="transform transition-all duration-300 hover:scale-105">
                  <ProjectCard {...project} />
                </div>
              ))}
            </div>
            
            {/* Right Column - Quote + Last 3 projects */}
            <div className="space-y-10">
              {/* Quote for offset */}
              <div className="text-gray-300 italic text-lg leading-relaxed px-6 py-8 rounded-lg">
                "Every project we undertake is a step towards building stronger, more resilient communities."
              </div>
              
              {filteredProjects.slice(3, 6).map((project) => (
                <div key={project.id} className="transform transition-all duration-300 hover:scale-105">
                  <ProjectCard {...project} />
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProjectsSection;
