import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { MdMenuOpen } from "react-icons/md";
import { UpdateFollower } from 'react-mouse-follower';
import { motion } from "framer-motion";
import { FaChevronDown } from "react-icons/fa";

const colors = {
  darkBlue: '#022b3a',
  blue: '#1f7a8c',
  lightBlue: '#bfdbf7',
  paleBlue: '#e1e5f2',
  white: '#ffffff',
};

// Array of menu items with dropdowns
const NavbarMenu = [
    { id: 1, title: "Home", link: "/" },
    { id: 2, title: "About Us", link: "/about",
        dropdown: [
            { id: 21, title: "Our Team", link: "https://prashraya.org/our-team/" },
        ]
    },
    { 
        id: 3, 
        title: "Events", 
        link: "#",
        dropdown: [
            { id: 31, title: "Past Events", link: "/past-events" },
            { id: 32, title: "Upcoming", link: "/upcoming-events" }
        ]
    },
    { 
        id: 4, 
        title: "Get Involved", 
        link: "#",
        dropdown: [
            { id: 41, title: "Volunteer", link: "/volunteer" },
            { id: 42, title: "Donate", link: "/donate" },
            { id: 43, title: "Intern", link: "/intern" }
        ]
    },
    { id: 5, title: "Contact", link: "/contact" },
];

const Navbar = () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [scrolled, setScrolled] = useState(false);
    const [isNavVisible, setIsNavVisible] = useState(true);

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 50) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    // Use the correct logoUrl here
    const logoUrl = 'https://prashraya.org/wp-content/uploads/elementor/thumbs/354150415_220248024272103_8189691252379910077_n-qtir2fw9zqtc7qrxft7est9yuk34qnuch8ihrepctc.jpg';

    return (
        <div className={`navbar-outer font-poppins transition-all duration-300 ${scrolled ? 'navbar-scrolled' : ''} ${!isNavVisible ? 'navbar-transparent-bg' : ''}`}>
            <motion.nav 
                initial={{opacity: 0}} 
                animate={{opacity: 1}}
                transition={{ duration: 1, delay: 0.5}}
                className="container w-full px-4 flex justify-between items-center"
            >
                {/* Logo and Desktop Menu Container - Hide when menu is not visible */}
                {isNavVisible && (
                    <div className="flex items-center gap-4">
                        {/* Logo */}
                        <Link to='/' className='text-xl font-bold uppercase flex items-center gap-2'>
                            <img src={logoUrl} alt="Aashraya Logo" className="h-12 w-auto" />
                            Aashraya
                        </Link>

                        {/* Desktop Menu */}
                        <div className='hidden md:block'>
                            <ul className='flex items-center gap-4'>
                                {NavbarMenu.map((item) => (
                                    <li key={item.id} className="relative group">
                                        <UpdateFollower mouseOptions={{
                                            backgroundColor: "white", 
                                            zIndex: 999,
                                            followSpeed: 1.7,
                                            scale: 5,
                                            mixBlendMode: "difference",
                                        }}>
                                            <Link 
                                                to={item.link} 
                                                className='inline-flex items-center text-base py-2 px-3 ml-2 uppercase transition-colors'
                                            >
                                                {item.title}
                                                {item.dropdown && <FaChevronDown className="ml-1 text-xs" />}
                                            </Link>
                                        </UpdateFollower>
                                        {/* Dropdown Menu - appears on hover of li or dropdown itself */}
                                        {item.dropdown && (
                                            <div className="absolute left-0 mt-2 w-48 rounded-md shadow-lg py-1 z-50 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-300 transform origin-top scale-95 group-hover:scale-100 bg-[#FFE1A8] text-[#723D46]">
                                                {item.dropdown.map((subItem) => (
                                                    <Link
                                                        key={subItem.id}
                                                        to={subItem.link}
                                                        className="block px-4 py-2 text-sm transition-colors hover:bg-[#E26D5C] hover:text-[#FFE1A8]"
                                                    >
                                                        {subItem.title}
                                                    </Link>
                                                ))}
                                            </div>
                                        )}
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                )}

                {/* Toggle Button (Desktop) - Always visible on desktop */}
                <button 
                    onClick={() => setIsNavVisible(!isNavVisible)}
                    className='text-white text-lg focus:outline-none bg-transparent border-none hidden md:block'
                    aria-label="Toggle navigation"
                    style={{ background: 'transparent', border: 'none' }}
                >
                   {isNavVisible ? 'Hide Menu' : 'Show Menu'}
                </button>

                {/* Mobile Menu Button */}
                <div className='md:hidden'>
                    <button 
                        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                        className='text-3xl'
                        aria-label="Toggle mobile menu"
                    >
                        <MdMenuOpen />
                    </button>
                </div>

                {/* Mobile Menu */}
                {isMobileMenuOpen && (
                   <div className="absolute top-full left-0 w-full md:hidden bg-[#723D46]">
                        <ul className="py-4">
                            {NavbarMenu.map((item) => (
                                <li key={item.id}>
                                    <Link 
                                        to={item.link}
                                        className="block px-4 py-2 transition-colors text-[#FFE1A8] hover:text-[#E26D5C]"
                                        onClick={() => setIsMobileMenuOpen(false)}
                                    >
                                        {item.title}
                                    </Link>
                                    {item.dropdown && (
                                        <ul className="pl-8">
                                            {item.dropdown.map((subItem) => (
                                                <li key={subItem.id}>
                                                    <Link
                                                        to={subItem.link}
                                                        className="block px-4 py-2 transition-colors text-[#723D46] hover:bg-[#E26D5C] hover:text-[#FFE1A8]"
                                                        onClick={() => setIsMobileMenuOpen(false)}
                                                    >
                                                        {subItem.title}
                                                    </Link>
                                                </li>
                                            ))}
                                        </ul>
                                    )}
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </motion.nav>
        </div>
    )
}

export default Navbar;
