import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { MdMenuOpen } from "react-icons/md";
import { motion } from "framer-motion";
import { FaChevronDown } from "react-icons/fa";

const colors = {
  darkBlue: '#022b3a',
  blue: '#1f7a8c',
  lightBlue: '#bfdbf7',
  paleBlue: '#e1e5f2',
  white: '#ffffff',
};

// Array of menu items with dropdowns
const NavbarMenu = [
    { id: 1, title: "Home", link: "/" },
    { id: 2, title: "About Us", link: "/about",
        dropdown: [
            { id: 21, title: "Our Team", link: "https://prashraya.org/our-team/" },
        ]
    },
    { 
        id: 3, 
        title: "Events", 
        link: "#",
        dropdown: [
            { id: 31, title: "Past Events", link: "/past-events" },
            { id: 32, title: "Upcoming", link: "/upcoming-events" }
        ]
    },
    { 
        id: 4, 
        title: "Get Involved", 
        link: "#",
        dropdown: [
            { id: 41, title: "Volunteer", link: "/volunteer" },
            { id: 42, title: "Donate", link: "/donate" },
            { id: 43, title: "Intern", link: "/intern" }
        ]
    },
    { id: 5, title: "Contact", link: "/contact" },
];

const Navbar = () => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isScrolled, setScrolled] = useState(false);
    const [isNavVisible, setIsNavVisible] = useState(true);
    // State to manage which desktop dropdown is open on hover
    const [activeDesktopDropdown, setActiveDesktopDropdown] = useState(null);
    const [leaveTimeoutId, setLeaveTimeoutId] = useState(null);

    // Function to clear the timeout and set active dropdown
    const handleMouseEnter = (itemId) => {
        if (leaveTimeoutId) {
            clearTimeout(leaveTimeoutId);
            setLeaveTimeoutId(null);
        }
        setActiveDesktopDropdown(itemId);
    };

    // Function to set timeout for hiding dropdown
    const handleMouseLeave = () => {
        const timeoutId = setTimeout(() => {
            setActiveDesktopDropdown(null);
        }, 200); // Increased delay (e.g., 200ms)
        setLeaveTimeoutId(timeoutId);
    };

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 50) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
            // Clean up timeout on component unmount
            if (leaveTimeoutId) {
                clearTimeout(leaveTimeoutId);
            }
        };
    }, [leaveTimeoutId]);

    // Use the correct logoUrl here
    const logoUrl = 'https://prashraya.org/wp-content/uploads/elementor/thumbs/354150415_220248024272103_8189691252379910077_n-qtir2fw9zqtc7qrxft7est9yuk34qnuch8ihrepctc.jpg';

    return (
        <div className={`navbar-outer transition-all duration-300 ${isScrolled ? 'navbar-scrolled' : ''} ${!isNavVisible ? 'navbar-transparent-bg' : ''}`} style={{ fontFamily: 'Helvetica Neue, sans-serif' }}>
            <motion.nav 
                initial={{opacity: 0}} 
                animate={{opacity: 1}}
                transition={{ duration: 1, delay: 0.5}}
                className={`container w-full px-4 flex items-center ${isNavVisible ? 'justify-between' : 'justify-end'}`}
            >
                {isNavVisible && (
                    <div className="flex items-center gap-4">
                        <Link to='/' className='text-xl font-bold uppercase flex items-center gap-2 mr-6'>
                            <img src={logoUrl} alt="Aashraya Logo" className="h-12 mr-2"/>
                            <span className="text-gray-200">Aashraya</span>
                        </Link>

                        <div className='hidden md:block'>
                            <ul className='flex items-center gap-4'>
                                {NavbarMenu.map((item) => (
                                    <li key={item.id} className="relative group">
                                        <Link 
                                            to={item.link} 
                                            className='inline-flex items-center text-base py-2 px-3 ml-2 uppercase transition-colors hover:text-[#D0EFB1]'
                                            onMouseEnter={() => handleMouseEnter(item.id)}
                                            onMouseLeave={handleMouseLeave}
                                        >
                                            {item.title}
                                            {item.dropdown && <FaChevronDown className="ml-1 text-xs" />}
                                        </Link>
                                        {/* Desktop Dropdown Menu */}
                                        {item.dropdown && activeDesktopDropdown === item.id && (
                                            <div 
                                                className="absolute left-0 top-full w-48 rounded-md shadow-lg py-1 z-[9999] bg-[#9DC3C2] text-[#4D7298] !opacity-100 !visible !pointer-events-auto"
                                                onMouseEnter={() => handleMouseEnter(item.id)} // Keep dropdown open on hovering over dropdown
                                                onMouseLeave={handleMouseLeave} // Hide dropdown when leaving dropdown
                                            >
                                                {item.dropdown.map((subItem) => (
                                                    <Link
                                                        key={subItem.id}
                                                        to={subItem.link}
                                                        className="block px-4 py-2 text-sm transition-colors hover:bg-[#77A6B6] hover:text-[#D0EFB1]"
                                                    >
                                                        {subItem.title}
                                                    </Link>
                                                ))}
                                            </div>
                                        )}
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                )}

                <button 
                    onClick={() => setIsNavVisible(!isNavVisible)}
                    className='text-white text-lg focus:outline-none bg-transparent border-none hidden md:block'
                    aria-label="Toggle navigation"
                    style={{ background: 'transparent', border: 'none' }}
                >
                   {isNavVisible ? 'Hide Menu' : 'Show Menu'}
                </button>

                <div className='md:hidden'>
                    <button 
                        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                        className='text-3xl text-[#D0EFB1]'
                        aria-label="Toggle mobile menu"
                    >
                        <MdMenuOpen />
                    </button>
                </div>

                {isMobileMenuOpen && (
                   <div className="absolute top-full left-0 w-full md:hidden bg-[#4D7298]">
                        <ul className="py-4">
                            {NavbarMenu.map((item) => (
                                <li key={item.id} className="relative group">
                                    <Link 
                                        to={item.link}
                                        className="block px-4 py-2 transition-colors text-[#D0EFB1] hover:text-[#B3D89C]"
                                        onClick={() => setIsMobileMenuOpen(false)}
                                    >
                                        {item.title}
                                        {item.dropdown && <FaChevronDown className="ml-1 text-xs inline" />}
                                    </Link>
                                    {item.dropdown && (
                                        <div className="absolute left-0 top-[calc(100%-2px)] w-48 rounded-md shadow-lg py-1 z-[1000] opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200 bg-[#9DC3C2] text-[#4D7298]">
                                            {item.dropdown.map((subItem) => (
                                                <Link
                                                    key={subItem.id}
                                                    to={subItem.link}
                                                    className="block px-4 py-2 text-sm transition-colors hover:bg-[#77A6B6] hover:text-[#D0EFB1]"
                                                    onClick={() => setIsMobileMenuOpen(false)}
                                                >
                                                    {subItem.title}
                                                </Link>
                                            ))}
                                        </div>
                                    )}
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </motion.nav>
        </div>
    )
}

export default Navbar;
